var min_bright = null;
var max_bright = null;
var new_bright = null;

var inc_dec_pct = 0.1;

function inc_bright(old_bright) {
    if (!old_bright) {
        return null;
    }

    // Add 1 in case off from last conversion
    var old_bright_pct = (old_bright+1)/255;
    var new_bright_pct = old_bright_pct + inc_dec_pct;
    // Want to round to nearest 10%
    new_bright_pct = Math.round(new_bright_pct*10)/10;

    var new_inc_bright = Math.round(new_bright_pct * 255);
    new_inc_bright = Math.min(new_inc_bright, 255);
    return new_inc_bright;
}


function dec_bright(old_bright) {
    if (!old_bright) {
        return null;
    }

    // Subtract 1 in case off from last conversion
    var old_bright_pct = (old_bright-1)/255;
    var new_bright_pct = old_bright_pct - inc_dec_pct;
    // Want to round to nearest 10%
    new_bright_pct = Math.round(new_bright_pct*10)/10;

    var new_dec_bright = Math.round(new_bright_pct * 255);
    new_dec_bright = Math.max(new_dec_bright, 1);
    return new_dec_bright;
}


msg.payload.forEach(function(value, index) {
    if (!(value.attributes.brightness)) {
        return;
    }

    if (min_bright===null ||
            value.attributes.brightness < min_bright) {
        min_bright = value.attributes.brightness;
    }

    if (max_bright===null ||
            value.attributes.brightness > max_bright) {
        max_bright = value.attributes.brightness;
    }
})


if (msg.config.inc_or_dec == 'increase') {
    new_bright = inc_bright(min_bright)
} else if (msg.config.inc_or_dec == 'decrease') {
    new_bright = dec_bright(max_bright)
}
// Else leave new_bright as null

if (new_bright===null) {
    return null;
}

msg.config.new_brightness = new_bright;

return msg;

