function add_brightness(entity, payload) {
    if (!flow.get('pending_brightness')) {
        return;
    }

    var pending_lights = flow.get('pending_brightness.pending_lights');
    var pending_index = pending_lights.indexOf(entity.entity_id);
    if (pending_index == -1) {
        return;
    }

    payload.data.brightness = flow.get('pending_brightness.brightness');
    // Delete pending light from list
    pending_lights.splice(pending_index, 1);

    if (pending_lights.length === 0) {
        flow.set('pending_brightness', undefined);
    }
}


function build_payload(entity, turn_on) {
    const payload = {};
    payload.domain = 'light';

    if (turn_on) {
        payload.service = 'turn_on';
    } else {
        payload.service = 'turn_off';
    }

    payload.data = {
        entity_id: entity.entity_id
    };

    if (turn_on) {
        add_brightness(entity, payload);
    }

    return payload;
}

const desired_lights_on = light_on_config[num_lights_on+1];

function add_light_msgs(turn_on) {
    msg.payload.forEach((e) => {
        if ((desired_lights_on.includes(e.entity_id) &&
                turn_on) || (!turn_on &&
                !desired_lights_on.includes(e.entity_id))) {
            const payload = build_payload(e, turn_on);
            new_msgs.push({ payload: payload});
        }
    });
}

// Want on first in case overload hub
add_light_msgs(true);
add_light_msgs(false);
return [new_msgs, null];

